T e.nld lx,\r\n    count(e.nld) gs\r\nFROM (\r\n        SELECT CASE\r\n                WHEN d.nl <= 30 THEN \'30岁以下\'\r\n                WHEN d.nl > 30\r\n                AND d.nl < 36 THEN \'31-35岁\'\r\n                WHEN d.nl > 35\r\n                AND d.nl < 41 THEN \'36-40岁\'\r\n                WHEN d.nl > 40\r\n                AND d.nl < 46 THEN \'41-45岁\'\r\n                WHEN d.nl > 45\r\n                AND d.nl < 51 THEN \'46-50岁\'\r\n                WHEN d.nl > 50\r\n                AND d.nl < 56 THEN \'50-55岁\'\r\n                WHEN d.nl > 55\r\n                AND d.nl < 61 THEN \'56-60岁\'\r\n                WHEN d.nl > 60\r\n                AND d.nl < 66 THEN \'60-65岁\'\r\n                WHEN d.nl > 65\r\n                AND d.nl < 71 THEN \'65-70岁\'\r\n                WHEN d.nl > 70 THEN \'70岁以上\'\r\n            END nld\r\n        FROM (\r\n                SELECT t.id,\r\n                    YEAR (now()) - YEAR (t.birthday) nl\r\n                FROM VillagePopulation t\r\n                WHERE t.isDeleted = 0\r\n                    AND t.id IN (\r\n                        SELECT a.populationId\r\n                        FROM VillageHouseCodeMember a\r\n                        WHERE a.isDeleted = 0\r\n                            AND a.householdId IN (\r\n                                SELECT c.householdId\r\n                                FROM FFPHouseholdCode c\r\n                                    LEFT JOIN VillageHouseholdCode h ON c.householdId = h.id\r\n                                WHERE h.areaId IN (\r\n                                        SELECT id\r\n                                        FROM BasicArea\r\n                                        WHERE idSequences LIKE @areaId\r\n                                    )\r\n                                    AND c.riskType = \'100B02002\'\r\n                                    AND c.isDeleted = 0\r\n                                    AND h.isDeleted = 0\r\n                       